import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  FlatList,
} from "react-native";
import React, { useState, useEffect } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import reusable from "../../components/Reusable/reusable.style";
import styles from "./search.style";
import { Feather, AntDesign } from "@expo/vector-icons";
import { COLORS } from "../../constants/theme";
import { AppBar, HeightSpacer } from "../../components";
import HotelCard from "../../components/Tiles/Hotels/HotelCard";
import axios from "axios";

const HotelSearch = ({navigation}) => {
  const [searchKey, setSearchKey] = useState('')
  const [searchResults, setSearchResults] = useState([])

  const handleSearch = async() =>{
    try {
      const response = await axios.get(`nativetravelappbackend-production.up.railway.app/api/hotels/search/${searchKey}`)
      setSearchResults(response.data)
    } catch (error) {
      console.log("Failed to get products" , error);
    }
  };

  return (
    <SafeAreaView >
      <View style={{height: 50}}>
      <AppBar
        top={10}
        left={20}
        right={20}
        title={'Look for hotels'}
        color={COLORS.white}
        icon={"filter"}
        color1={COLORS.white}
        onPress={() => navigation.goBack()}
        onPress1={() => {}}
      />
      </View>
      <View style={styles.searchContainer}>
        <View style={styles.searchWrapper}>
          <TextInput
            style={styles.input}
            value={searchKey}
            onChangeText={setSearchKey}
            placeholder="Where do you want to stay"
          />
        </View>

        <TouchableOpacity style={styles.searchBtn} onPress={handleSearch}>
          <Feather name="search" size={24} color={COLORS.white} />
        </TouchableOpacity>
      </View>

      {searchResults.length === 0 ? (
        <View>
          <HeightSpacer height={"20%"} />
          <Image
            source={require("../../assets/images/search.png")}
            style={styles.searchImage}
          />
        </View>
      ) : (
         <View style={{paddingLeft: 12}}>
           <FlatList
          data={searchResults}
          keyExtractor={(item) => item._id}
          numColumns={2}
          ItemSeparatorComponent={()=> (<View style={{height: 16}}/>)}
          renderItem={({ item }) => (
            <HotelCard
              item={item}
              margin={10}
              onPress={() => navigation.navigate("HotelDetails", item._id)}
            />
          )}
        />
         </View>
      )}
    </SafeAreaView>
  );
};

export default HotelSearch;
